    LANGUAGE plpgsql
    AS '
DECLARE
    DECLARE
    row hs_office.coopassettx;
    grantsAfter numeric;
    grantsBefore numeric;
BEGIN
    SELECT count(*) INTO grantsBefore FROM rbac.grant;

    FOR row IN SELECT * FROM hs_office.coopassettx LOOP
            
